import React from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import Homepage from './Homepage';
import Login from './Login';
import Signup from './Signup';
import Header from './Header';
import Medicines from './Medicines';
import Test from './Test';
import Details from './Details';
import Box from './Box';
import Cart from './Cart';
import Cartdeets from './Cartdeets';
import Consult from './Consult';
import Cancer from './Cancer';
import Lab from './Lab';
import AboutUs from './AboutUs';
import SearchResults from './SearchResults';
import LocationUpdater from './LocationUpdater';
import { useState } from 'react';

function App() {
  const [showLocationModal, setShowLocationModal] = useState(false);
  const [userLocation, setUserLocation] = useState(''); // To store the user's updated location

  const handleLocationClick = () => {
    setShowLocationModal(true); // Open the modal when clicked
  };

  const handleClose = () => {
    setShowLocationModal(false); // Close the modal
  };

  const handleLocationUpdate = (newLocation) => {
    setUserLocation(newLocation); // Update the location in the state
    setShowLocationModal(false); // Close the modal after updating
  };
  return (
    <div>
      <BrowserRouter>
      <Header onLocationClick={handleLocationClick} />
        <LocationUpdater show={showLocationModal} handleClose={handleClose} />
        <Routes>
          <Route path="/Homepage" element={<Homepage />} />
          <Route path="/Login" element={<Login />} />
          <Route path="/Signup" element={<Signup />} />
          <Route path="/Medicines" element={<Medicines />} />
          <Route path="/Test" element={<Test />} />
          <Route path="/Details" element={<Details />} />
          <Route path="/Box" element={<Box />} />
          <Route path="/Cart" element={<Cart />} />
          <Route path="/Cartdeets" element={<Cartdeets />} />
          <Route path="/Consult" element={<Consult />} />
          <Route path="/Cancer" element={<Cancer />} />
          <Route path="/Lab" element={<Lab />} />
          <Route path="/aboutus" element={<AboutUs />} />
          <Route path="/search-results" element={<SearchResults />} />

        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
