import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Medicines.css'; // Assuming you're creating a separate CSS file for styles

const Medicines = () => {
  const [medicines, setMedicines] = useState([]);
  const [currentPage, setCurrentPage] = useState(1); // State for current page
  const medicinesPerPage = 20; // Display 20 medicines per page

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/medicines');
        setMedicines(response.data.data); // Assuming the data is in response.data.data
      } catch (error) {
        console.error('Error fetching medicines:', error);
      }
    };

    fetchData();
  }, []);

  // Pagination logic
  const indexOfLastMedicine = currentPage * medicinesPerPage;
  const indexOfFirstMedicine = indexOfLastMedicine - medicinesPerPage;
  const currentMedicines = medicines.slice(indexOfFirstMedicine, indexOfLastMedicine);
  const totalPages = Math.ceil(medicines.length / medicinesPerPage);

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="medicines-container">
      {currentMedicines.length > 0 ? (
        currentMedicines.map((medicine) => (
          <div key={medicine._id} className="medicine-card">
            <img src={medicine.img} alt={medicine.name} className="medicine-image" />
            <h3>{medicine.name}</h3>
            <p className="price">MRP {medicine.price} <span className="discount">58% off</span></p>
            <div className="buttons">
              <button className="cart-btn">Cart</button>
              <button className="details-btn">Details</button>
            </div>
          </div>
        ))
      ) : (
        <p>No medicines available</p>
      )}

      {/* Pagination Controls */}
      <div className="pagination-container">
        <div className="pagination">
          <button onClick={handlePreviousPage} disabled={currentPage === 1}>
            Previous
          </button>
          <span>Page {currentPage} of {totalPages}</span>
          <button onClick={handleNextPage} disabled={currentPage === totalPages}>
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default Medicines;
