import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Medicines.css'; // Optional for styling

const Medicines = () => {
  const [medicines, setMedicines] = useState([]);

  useEffect(() => {
    // Fetch data from the backend API
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/list');
        setMedicines(response.data);
      } catch (error) {
        console.error('Error fetching data', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="medicines-container">
      <div className="medicines-grid">
        {medicines.map((list) => (
          <div key={list._id} className="medicine-card">
            <img src={list.image} alt={list.name} className="medicine-image" />
            <h3>{list.name}</h3>
            <p>{list.quantity}</p>
            <p className="price">MRP {list.price} <span className="discount">58% off</span></p>
            <div className="buttons">
              <button className="cart-btn">Cart</button>
              <button className="details-btn">Details</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Medicines;
