import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Grid, Card, CardContent, CardActions, Typography, Button, Box } from '@mui/material';
import './Header.css'
const LabTest = () => {
  const [labTests, setLabTests] = useState([]);
  const [editingTest, setEditingTest] = useState(null);

  useEffect(() => {
    fetchLabTests();
  }, []);

  const fetchLabTests = async () => {
    try {
      const response = await axios.get('http://localhost:3000/labtests');
      setLabTests(response.data.data);
    } catch (error) {
      console.error('Error fetching lab tests:', error);
    }
  };

  const updateLabTest = async (id) => {
    try {
      const response = await axios.put(`http://localhost:3000/labtests/${id}`, editingTest);
      setLabTests(labTests.map((test) => (test._id === id ? response.data.data : test)));
      setEditingTest(null);
    } catch (error) {
      console.error('Error updating lab test:', error);
    }
  };

  const deleteLabTest = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/labtests/${id}`);
      setLabTests(labTests.filter((test) => test._id !== id));
    } catch (error) {
      console.error('Error deleting lab test:', error);
    }
  };

  return (<div className='hihi'>
    <Container maxWidth=" lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h3" align="center" gutterBottom>
        Lab Tests
      </Typography>

      <Grid container spacing={4}>
        {labTests.map((test) => (
          <Grid item key={test._id} xs={12} sm={6} md={4}>
            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column', borderRadius: 4, boxShadow: 6 }}>
              <CardContent sx={{ flexGrow: 1 }}>
                {editingTest && editingTest._id === test._id ? (
                  <Box>
                    {/* This section can be replaced with a more stylish editing feature if needed */}
                    <Typography variant="h6" gutterBottom>Edit Mode</Typography>
                    {/* Add editing elements here if necessary */}
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={() => updateLabTest(test._id)}
                      sx={{ mr: 2 }}
                    >
                      Save
                    </Button>
                    <Button
                      variant="outlined"
                      color="secondary"
                      onClick={() => setEditingTest(null)}
                    >
                      Cancel
                    </Button>
                  </Box>
                ) : (
                  <Box>
                    <Typography gutterBottom variant="h5" component="div" sx={{ fontWeight: 'bold' }}>
                      {test.testName}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {test.description}
                    </Typography>
                    <Typography variant="h6" color="primary" sx={{ mt: 2 }}>
                      Price: ${test.price}
                    </Typography>
                  </Box>
                )}
              </CardContent>
              <CardActions sx={{ justifyContent: 'center', pb: 2 }}>
                {!editingTest && (
                  <>
                    <Button size="large" variant="contained" color="primary" onClick={() => setEditingTest(test)}>
                      Add to Cart
                    </Button>

                  </>
                )}
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
    </div>
  );
};

export default LabTest;
