import React, { useEffect, useState } from 'react';

function List() {
  const [items, setItems] = useState([]); // Initialize items as an empty array

  useEffect(() => {
    fetch('http://localhost:3000/api/list')
      .then(response => response.json())
      .then(data => {
        console.log(data); // Log the data to inspect its structure
        setItems(data); // Store the data in state
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  return (
    <div>
      <h2>List of Items</h2>
      <div>
        {Array.isArray(items) ? ( // Check if items is an array before mapping
          items.map((item, index) => (
            <div key={index} className="mt-2 mb-2 p-3 w-50 m-2 shadow">
              <img className="pic" src={item.image} alt={item.name} />
              <p>{item.name}</p>
              <p className="fw-light">{item.quantity}</p>
              <div className="d-flex">
                <p className="me-2">MRP</p>
                <p className="text-danger">{item.price}</p>
              </div>
            </div>
          ))
        ) : (
          <p>No items found.</p>
        )}
      </div>
    </div>
  );
}

export default List;
