import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Medicines.css'; // Assuming you're creating a separate CSS file for styles

const Medicines = () => {
  const [medicines, setMedicines] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
const [itemsPerPage] = useState(10); // Show 10 items per page
const indexOfLastItem = currentPage * itemsPerPage;
const indexOfFirstItem = indexOfLastItem - itemsPerPage;
const currentMedicines = medicines.slice(indexOfFirstItem, indexOfLastItem);


  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/medicines?page=${currentPage}&limit=${itemsPerPage}`);

        setMedicines(response.data.data); // Assuming the data is in response.data.data
      } catch (error) {
        console.error('Error fetching medicines:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="medicines-container">
      {medicines.length > 0 ? (
        medicines.map((medicine) => (
          <div key={medicine._id} className="medicine-card">
            <img src={medicine.img} alt={medicine.name} className="medicine-image" />
            <h3>{medicine.name}</h3>
            <p className="price">MRP {medicine.price} <span className="discount">58% off</span></p>
            <div className="buttons">
              <button className="cart-btn">Cart</button>
              <button className="details-btn">Details</button>
            </div>
          </div>

        ))
      ) : (
        <p>No medicines available</p>
      )}
    </div>
  );
};

export default Medicines;
