import React, { useState } from 'react';
import { Container, IconButton, Button, Typography, Box, Divider } from '@mui/material';
import RemoveCircleOutlineIcon from '@mui/icons-material/RemoveCircleOutline';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import DeleteIcon from '@mui/icons-material/Delete';
import HomeIcon from '@mui/icons-material/Home';

const App = () => {
  // State to manage the quantity of items
  const [quantity, setQuantity] = useState(1);

  const items = [
    {
      name: "Maxirich",
      price: "₹257",
      discount: "28% off",
      originalPrice: "₹357",
      rating: "4.4 ★ (764)",
      image: "https://via.placeholder.com/80?text=Maxirich",
    },
    {
      name: "Omnigel",
      price: "₹183",
      discount: "28% off",
      originalPrice: "₹254.1",
      rating: "4.5 ★ (3240)",
      image: "https://via.placeholder.com/80?text=Omnigel",
    },
    {
      name: "Nicotex",
      price: "₹328",
      discount: "11% off",
      originalPrice: "₹368",
      rating: "4.2 ★ (190)",
      image: "https://via.placeholder.com/80?text=Nicotex",
    },
    {
      name: "Endura Mass",
      price: "₹1299",
      discount: "",
      originalPrice: "",
      rating: "3.8 ★ (471)",
      image: "https://via.placeholder.com/80?text=Endura+Mass",
    },
    {
      name: "Cofsils",
      price: "₹29",
      discount: "17% off",
      originalPrice: "₹35",
      rating: "4.3 ★ (1856)",
      image: "https://via.placeholder.com/80?text=Cofsils",
    },
  ];

  // Function to increase the quantity
  const handleAdd = () => {
    setQuantity(prevQuantity => prevQuantity + 1);
  };

  // Function to decrease the quantity
  const handleRemove = () => {
    if (quantity > 1) {
      setQuantity(prevQuantity => prevQuantity - 1);
    }
  };

  return (
    <Container maxWidth="lg" sx={{ padding: 0, margin: 0 }}>
      <Box sx={{ display: 'flex', marginTop: 4, width: '100%',mr:12 }}>
        {/* Left Section */}
        <Box sx={{ flex: 3, paddingRight: 2, borderRight: '1px solid #ddd' }}>
          {/* Item details section */}
          <Box mb={4}>
            <Typography variant="h6">1 item added</Typography>
            <Typography variant="body2" color="textSecondary">
              Items not requiring prescription
            </Typography>
            <Box sx={{ display: 'flex', alignItems: 'center', marginY: 2 }}>
              <img
                src="https://via.placeholder.com/60"
                alt="Dolo 650 Tablet"
                style={{ width: 60, height: 60, marginRight: 16 }}
              />
              <Box>
                <Typography variant="body1" fontWeight="bold">
                  Dolo 650 Tablet
                </Typography>
                <Typography variant="body2">Strip of 15 tablets</Typography>
              </Box>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <Typography variant="h6" color="primary">
                ₹29
              </Typography>
              <Typography variant="body2" color="textSecondary" sx={{ textDecoration: 'line-through', marginX: 2 }}>
                ₹33.76
              </Typography>
              <Typography variant="body2" color="green">
                14% off
              </Typography>
              <Box sx={{ display: 'flex', alignItems: 'center', marginLeft: 'auto', gap: 1 }}>
                <IconButton onClick={handleRemove} sx={{ borderRadius: '4px' }}>
                  <RemoveCircleOutlineIcon />
                </IconButton>
                <Typography variant="body1" sx={{ borderRadius: '4px', padding: '2px 8px' }}>
                  {quantity}
                </Typography>
                <IconButton onClick={handleAdd} sx={{ borderRadius: '4px' }}>
                  <AddCircleOutlineIcon />
                </IconButton>
