import React, { useState } from "react";
import img15 from './image/img15.webp';
import styled from "styled-components";
import { Dropdown, Modal, Button } from "react-bootstrap";
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import img16 from './image/img16.png';
import img17 from './image/img17.webp';
import img18 from './image/img18.webp';
import img19 from './image/img19.webp';
import img20 from './image/img20.webp';
import img21 from './image/img21.webp';
import Carousel from 'react-bootstrap/Carousel';
import { CarouselItem } from 'react-bootstrap';

function Details() {
  const [value, setValue] = useState('offer');
  const [showMembershipModal, setShowMembershipModal] = useState(false);
  const [selectedMembership, setSelectedMembership] = useState(null);

  const handleChange = (event) => {
    setValue(event.target.value);
  };

  const handleMembershipClose = () => setShowMembershipModal(false);
  const handleMembershipShow = () => setShowMembershipModal(true);

  const handleMembershipSelect = (membership) => {
    setSelectedMembership(membership);
  };

  return (
    <div>
      <Design4>
        <div className='boxx d-flex'>
          <div className='w-25 mt-2 ms-3 ps-4 pe-4 '>
            <div className='w-100 mt-5 ms-1 ps-4 pe-4 border shadow'>
              <h4 className="mt-2">Overview of Dolo 650 Tablet</h4>
              <p>Uses and benefits</p>
              <p>Side effects</p>
              <p>How to use</p>
              <p>How drug works</p>
              <p>Safety advice</p>
              <p>Missed dose</p>
              <p>Alternate Brands of Dolo 650 Tablet</p>
              <p>Quick tips</p>
              <p>Fact box</p>
              <p>Interaction with drugs</p>
              <p>Patient concerns</p>
            </div>
            <div className='w-100 mt-4 ms-1 ps-4 pe-4 border shadow'>
              <h6 className="mt-3">Note</h6>
              <p>Consult the doctor if any of these side effects persist or become bothersome...</p>
            </div>
            {/* Moved SUBSTITUTES section */}
