// LocationUpdater.js
import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import './'

const LocationUpdater = ({ show, handleClose }) => {
  const [location, setLocation] = useState('');

  const handleUpdateLocation = async () => {
    try {
      const response = await axios.post('http://localhost:3000/api/update-location', { location });
      console.log(response.data.message);
      handleClose(); // Close the modal after successful submission
    } catch (error) {
      console.error('Error updating location:', error);
    }
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Update Your Location</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <input
          type="text"
          placeholder="Enter your location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="form-control"
        />
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleUpdateLocation}>
          Update Location
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default LocationUpdater;
