import React, { useState, useEffect } from 'react';
import { Modal, Button } from 'react-bootstrap';
import "./Location.css"
const LocationUpdater = ({ onClose }) => {
  const [error, setError] = useState(null);
  const [location, setLocation] = useState('');

  const handleChange = (e) => {
      setLocation(e.target.value);
  };

  const handleSubmit = () => {
      fetch('http://localhost:3000/update-location', {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json',
          },
          body: JSON.stringify({ location }),
      })
      .then(response => response.json())
      .then(data => console.log('Location updated successfully:', data))
      .catch(error => console.error('Error updating location:', error));
  };

  useEffect(() => {
    // Check if the browser supports the Geolocation API
    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          // Use latitude and longitude to reverse geocode or simply set it
          setLocation(`Lat: ${latitude}, Long: ${longitude}`);
        },
        (err) => {
          setError('Location access denied.');
        }
      );
    } else {
      setError('Geolocation is not supported by this browser.');
    }
  }, []);

  const handleSaveLocation = () => {
    // You can send the location data to the backend or simply close the modal
    console.log('Location saved:', location);
    onClose(); // Close the modal
  };

  return (
    <Modal show onHide={onClose}>
      <Modal.Header closeButton>
        <Modal.Title>Update Your Location</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {error ? (
          <p>{error}</p>
        ) : location ? (
          <p>Your current location: {location}</p>
        ) : (
          <p>Fetching your location...</p>
        )}
        <input
          type="text"
          value={location}
          onChange={(e) => setLocation(e.target.value)} // Allow manual override
          className="form-control"
          placeholder="Enter your location manually"
        />
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onClose}>
          Cancel
        </Button>
        <Button variant="primary" onClick={handleSaveLocation}>
          Save Location
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default LocationUpdater;
